var turns=0,player=1,cards=document.getElementsByClassName("card"),pioche=document.getElementById("pioche");window.addEventListener("load",function(){Array.from(cards).forEach(function(e){e.addEventListener("click",function(){"true"===e.dataset.flipable?(console.log(e.dataset.flipable),e.classList.add("flipped"),e.dataset.flipable=!1):(e.classList.remove("flipped"),e.dataset.flipable=!0)})}),pioche.addEventListener("click",function(){Array.from(cards).forEach(function(e){console.log(e.parentElement)})})});